#1 nombre de handles
def account_size(account)
return account.size
end

#2 Plus petit
def smallest(account)
min = 100
valeur = ""
account.each do |i|
	if i.size < min
		min = i.size
		valeur = i
	end
end
return valeur , min
end

#3 5 caractères
def five_characters_accounts(account)
length5 = []
value = ""
account.each do |i|
	if i.size.to_i == 6
		length5.push i
	end
end
return length5
end

#4 Majuscule
def uppercase(account)
arr = [*("A".."Z")]
count = 0
account.each do |i|
	if arr.include? i.slice(1,1)
	count += 1
	end
end
return count
end

#5 Trie AZ
def sort_alpha(account)
return account.sort
end

#6 Trie taille
def sort_size(account)
sorted = []
34.times do |j|
account.each do |i|
	if i.size == j
		sorted.push i
	end
end
end
return sorted
end

#7 trouve epenser
def epenser(account)
value = ""
count = 1
account.each do |i|
	if i == "@epenser"
		value = count
	end
	count += 1
end
return value
end

#8 nb caractères
def characters_by_element(account)
first = []
second = []
34.times do |j|
	count = 0 
	account.each do |i|
	if i.size == j
		count += 1
		first.push[j] = "Nombre de comptes comprenant #{j} caractères : #{count}"
	end
end
end
first.each do |i|
	unless i == nil
	second.push i
end
end
return second
end

