elsif nb == 3
	puts "Voici une liste des comptes faisant 5 caractères :", five_characters_accounts(account)
elsif nb == 4
	puts "Il y a #{uppercase(account)} comptes comprenant une lettre majuscule comme premier caractère"
elsif nb == 5
	puts "Voici la liste de comptes triée par ordre alphabétique :", sort_alpha(account)
elsif nb == 6
	puts "Voici la liste de comptes triée par taille :", sort_size(account)
elsif nb == 7
	puts "Le compte @epenser se retrouve à la position n°#{epenser(account)}"
elsif nb == 8
	puts characters_by_element(account)
else 
	exit
end
puts "-" * 20
end









